mapper.select:
SELECT t1.id, t1.goods_id, t1.store_id, t1.in_total, t1.out_total, t1.outting_total, t1.update_time, t1.create_time, t2.name store_name, t3.name goods_name FROM wms_store_goods_stat t1 LEFT JOIN wms_store t2 on t1.store_id = t2.id LEFT JOIN wms_goods t3 on t1.goods_id = t3.id WHERE 1 = 1
AND t1.store_id = #{filter.storeId}
AND t1.goods_id = #{filter.goodsId}
ORDER BY t1.create_time DESC

mapper.update:
UPDATE wms_store_goods_stat
SET outting_total = outting_total + #{goodsNum}
WHERE store_id = #{storeId} AND goods_id = #{goodsId}

UPDATE wms_store_goods_stat
SET outting_total = outting_total - #{goodsNum}, out_total = out_total + #{goodsNum}
WHERE store_id = #{storeId} AND goods_id = #{goodsId}

UPDATE wms_store_goods_stat
SET outting_total = outting_total - #{goodsNum}
WHERE store_id = #{storeId} AND goods_id = #{goodsId}

UPDATE wms_store_goods_stat
SET out_total = out_total - #{goodsNum}
WHERE store_id = #{storeId} AND goods_id = #{goodsId}

UPDATE wms_store_goods_stat
SET out_total = out_total + #{goodsNum}
WHERE store_id = #{storeId} AND goods_id = #{goodsId}
